impo

const usernameSlice=createSlice({
    name:'username',
    initialState:'',
    reducers:{
        change:(state,action)=>{
            return action.payload

        }
    },
    extraReducers:{
        logout:()=>{
            return false;
        }
    }
})

export const {change}=usernameSlice.actions

export default usernameSlice.reducer;
