import React, { useEffect, useState } from 'react'
import Footer from '../Footer/Footer'
import AdminHeader from '../Header/AdminHeader';
import axios from '../../../utils/axios'
import './updateUser.css'
import { adminEditUser, adminUpdateUser } from '../../../utils/Constants';
import { useNavigate, useParams } from 'react-router-dom';
import Swal from 'sweetalert2'

function UpdateUser() {
  const params=useParams();
  const navigate=useNavigate();
  const [userName,setUserName]= useState('');
  const [email,setEmail] = useState('');

  useEffect(()=>{
      axios.get(`${adminEditUser}/${params.id}`).then((res)=>{
          console.log(res.data.userData);
          setEmail(res.data.userData.email);
          setUserName(res.data.userData.userName);
      }).catch((err)=>{
        alert(err)
      })
  },[])


  const updateUserDetails=async(e)=>{
    const body={userName:userName,email:email}
    e.preventDefault(e);
    const swalWithBootstrapButtons = Swal.mixin({
      customClass: {
        confirmButton: 'btn btn-success',
        cancelButton: 'btn btn-danger'
      },
      buttonsStyling: false
    })
    
    swalWithBootstrapButtons.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'Yes, Update it!',
      cancelButtonText: 'No, cancel!',
      reverseButtons: true
    }).then((result) => {
      if (result.isConfirmed) {
        console.log(body)
        axios.put(`${adminUpdateUser}/${params.id}`,body, { headers: { "Content-Type": "application/json" } }).then((response)=>{
            console.log(response.data)
          swalWithBootstrapButtons.fire(
            'Updated!',
            'User file has been Updated.',
            'success'
          )
          navigate('/users')
        }).catch((err)=>{
          Swal.fire({
            title: 'Oops...',
            text: "try again",
            height: "5rem",
        })
        })
      } else if (
        /* Read more about handling dismissals below */
        result.dismiss === Swal.DismissReason.cancel
      ) {
        swalWithBootstrapButtons.fire(
          'Cancelled',
          'User is not updated:)',
          'error'
        )
      }
    })
    
    
  }


  return (
    <div>
      <AdminHeader />

      <form className='updateForm' onSubmit={(e)=>updateUserDetails(e)}>

        <div className="container1">
          <h1>UPDATE USER</h1>

          <label for="username"><b>Username</b></label>
          <input
            type="text"
            placeholder="Enter username"
            value={userName}
            onChange={(e)=>setUserName(e.target.value)}
            id="username"
            required=""
          />

          <label for="email"><b>Email</b></label>
          <input
            type="text"
            placeholder="Enter Email"
            value={email}
            onChange={(e)=>setEmail(e.target.value)}
            id="email"
            required=""
          />

          <button type="submit">Update User</button>
        </div>


      </form>
      <Footer />
    </div>
  )
}

export default UpdateUser
