import { createSlice } from "@reduxjs/toolkit";
const Initial_State='https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg';


const userimageSlice = createSlice({
    name:'userimage',
    initialState: Initial_State,
    reducers:{
        imageChange : (state,action)=>{
            console.log('imageChange function inside reducer hitting',action)
            return action.payload;
            
        }
    },
    extraReducers:{
        logout:()=>{

            return 'https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg' ;
        }
    }
})

export const {imageChange} = userimageSlice.actions;
export default userimageSlice.reducer;
