import React, { useEffect, useState } from 'react'
import Footer from '../Footer/Footer'
import AdminHeader from '../Header/AdminHeader'
import axios from '../../../utils/axios'
import './users.css'
import { adminDeleteUser, admingetAllusers } from '../../../utils/Constants'
import { useNavigate } from 'react-router-dom'

function Usermanagement() {

    const navigate=useNavigate()
    const [users,setUsers]=useState([])

    useEffect((key)=>{
        getUserLists();

    },[])

    const getUserLists = ()=>{
            axios.get(admingetAllusers).then((response)=>{
                setUsers(response.data.users)
                console.log(response.data.users)
            }).catch((err)=>{
                console.log("oops user catch client");

            })
    }


    const deleteUser=(id)=>{
        axios.delete(`${adminDeleteUser}/${id}`)
    }


    return (
        <div>
            <AdminHeader />

            <h1>A Fancy Table</h1>

            <table id="customers">
                <tr>
                    <th>No</th>
                    <th>User Name</th>
                    <th>Emaiil</th>
                    <th>Action</th>
                    <th>Action</th>
                </tr>
               
                  {  users.map((obj,index)=>
                   <tr>
                    <td>{index+1}</td>
                    <td>{obj.userName}</td>
                    <td>{obj.email}</td>
                    <td>
                        <button onClick={()=>deleteUser(obj._id)}>Edit</button>
                    </td>
                    <td>
                        <button onClick={()=>navigate(`/updateUser/${obj._id}`)} >Delete</button>
                    </td>
                    </tr>
                  )}

                

            </table>
            <Footer />
        </div>
    )
}

export default Usermanagement
